var login = require('./login');
var signup = require('./signup');
var OrgUser = require('../models/organisation');
var Sponsor = require('../models/sponsor');

module.exports = function(passport){

	// Passport needs to be able to serialize and deserialize users to support persistent login sessions
    passport.serializeUser(function(orguser, done) {
        console.log('serializing orguser: ');console.log(orguser);
        done(null, orguser._id);
    });

    passport.deserializeUser(function(id, done) {
        OrgUser.findById(id, function(err, orguser) {
            console.log('deserializing orguser:',orguser);
            done(err, orguser);
        });
    });

    // Setting up Passport Strategies for Login and SignUp/Registration
    login(passport);
    signup(passport);

}

